import React from 'react';
import {AppContext} from "../App/AppProvider";

export default function ({firstVisit}) {
    return(
        <AppContext.Consumer>
            {({firstVisit}) =>
                firstVisit ? <div>
                    Welcome to CryptoDash, Please select your favorite coin to begin with. {'  '}
                </div> : null
            }
        </AppContext.Consumer>
    );
};
